import React, { useState } from "react";
import { useRecoilState, useRecoilValue } from "recoil";
import {
  currentCategoryState,
  categoryOptionsState,
  toDoSelector,
  toDoState,
} from "./atoms";
import CreateToDo from "./CreateToDo";
import ToDo from "./ToDo";

function ToDoList() {
  //const toDos = useRecoilValue(toDoState);
  //console.log(toDos);
  const toDos = useRecoilValue(toDoSelector);
  const categories = useRecoilValue(categoryOptionsState);
  const [category, setCategory] = useRecoilState(currentCategoryState);
  const onInput = (event: React.FormEvent<HTMLSelectElement>) => {
    setCategory(event.currentTarget.value as any);
  };
  const onDelete = (event: React.FormEvent<HTMLButtonElement>) => {
    //클릭한 버튼 옆의 toDo를 toDo.id로 연결해서 toDo 삭제하기
  };
  return (
    <div>
      <h1>To Dos</h1>
      <hr />
      <select value={category} onInput={onInput}>
        {categories.map((categoryOption) => (
          <option value={categoryOption}>{categoryOption}</option>
        ))}
      </select>
      <CreateToDo />
      {toDos?.map((toDo) => (
        <>
          <ToDo key={toDo.id} {...toDo} />
          <button key={toDo.id} onClick={onDelete}>
            Delete
          </button>
        </>
      ))}
    </div>
  );
}

export default ToDoList;
