import React, { useState } from "react";
import { useRecoilState, useRecoilValue, useSetRecoilState } from "recoil";
import {
  type IToDo,
  currentCategoryState,
  categoryOptionsState,
  toDoSelector,
  toDoState,
} from "./atoms";
import CreateToDo from "./CreateToDo";
import ToDo from "./ToDo";

function ToDoList() {
  //const toDos = useRecoilValue(toDoState);
  //console.log(toDos);
  const filteredToDos = useRecoilValue(toDoSelector);
  const setTodos = useSetRecoilState(toDoState);
  const categories = useRecoilValue(categoryOptionsState);
  const [category, setCategory] = useRecoilState(currentCategoryState);
  const onChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setCategory(event.currentTarget.value as any);
  };

  const deleteTodo = (targetId: IToDo["id"]) => {
    setTodos((old) => old.filter((toDo) => toDo.id !== targetId));
  };

  return (
    <div>
      <h1>To Dos</h1>
      <hr />
      <div>
        {categories.map((categoryOption) => (
          <label>
            <input
              onChange={onChange}
              type="radio"
              name="category"
              key={categoryOption}
              value={categoryOption}
              checked={categoryOption === category}
            />
            {categoryOption}
            {String(categoryOption === category)}
          </label>
        ))}
      </div>
      <CreateToDo />
      {filteredToDos?.map((toDo) => (
        <>
          <ToDo key={toDo.id} {...toDo} />
          <button
            key={toDo.id}
            onClick={(e) => {
              deleteTodo(toDo.id);
            }}
          >
            Delete
          </button>
        </>
      ))}
    </div>
  );
}

export default ToDoList;
